        [TestCase("сorrectlyInfiniteTimer")]
        public void CorrectlyInfiniteTimer(string htmlFile)
        {
            var editionsHtml = GetHtml(htmlFile);
            var edition = SteamNetworkService.ParseEditions(editionsHtml).FirstOrDefault();
            var g =  new Game { SubId = "492793" };

            SteamNetworkService.ParseHtmlDiscountCountdown(edition, g);

            Assert.That(g.DiscountEndTimeUtc == DateTime.MaxValue);
        }

        [TestCase("notEndlessDiscount", "664383")]
        [TestCase("bundleDiscount", "12620")]
        public void NoEndlessTimer(string htmlFile, string subId)
        {
            var editionsHtml = GetHtml(htmlFile);
            var edition = SteamNetworkService.ParseEditions(editionsHtml).FirstOrDefault();
            var g = new Game { SubId = subId };

            SteamNetworkService.ParseHtmlDiscountCountdown(edition, g);

            Assert.That(g.DiscountEndTimeUtc != DateTime.MaxValue);
        }

        [TestCase("noDiscountGame", false)]
        [TestCase("сorrectlyInfiniteTimer", true)]
        public void CustomTestIsDiscount(string htmlFile, bool isCorrect)
        {
            htmlFile = GetHtml(htmlFile);

            var edition = SteamNetworkService.ParseEditions(htmlFile).FirstOrDefault();

            var isDiscount = edition.Contains("game_purchase_discount");

            Assert.That(isDiscount == isCorrect);
        }


